INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1167", "null", "Adaptació d'activitats Babel","Primària, Secundària", "", "El programa Babel és una aplicació per a MS-DOS que el PIE va adquirir i distribuir a centres de primària i secundària. Una de les novetats del Clic 3.0 són les activitats de text, que permeten passar a un entorn gràfic i multimèdia gairebé tots els exercicis que fins ara s'havien creat amb el programa Babel. Aquests paquets d'activitats són adaptacions directes dels materials Babel que fins ara s'havien publicat al CD-ROM 'Sinera en Disc'. Cal advertir que no aprofiten plenament les noves possibilitats del Clic (dictats amb sons, integració de recursos multimèdia, gràfics, ajudes flotants, modalitats d'avaluació...) ja que el seu objectiu és només donar continuïtat a uns valuosos materials didàctics que fins ara només estaven disponibles per a l'entorn Babel.", "Català", "/var/lib/jclic_browser/zips/catpri/catpri.jclic.inst");

