INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1342", "numeros2.gif", "Jugando con los números","Infantil", "Matemáticas", "Paquete de actividades dirigido al alumnado de educación infantil, que trabaja fundamentalmente la adquisición del concepto del número, la comparación de cantidades, la ordenación de grupos en función del número de objetos, el anterior y el posterior. Las actividades se realizan con gráficos: objetos, manos, dados, números, y con sonidos. El contenido textual es mínimo, estando los mensajes explicativos de cada actividad en forma de pictogramas. Objetivos: Reforzar y ampliar el aprendizaje de los números. Respetar el ritmo personal del alumnado. Favorecer el trabajo individual. Adquirir la noción de cantidad de los números del 0 al 9.", "euskara", "/var/lib/jclic_browser/zips/eusnum2/eusnum2.jclic.inst");

