INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1169", "selva.jpg", "El gran llibre de la Selva","Primària", "", "El Gran llibre de la Selva és un treball d'explotació de textos feta amb el Clic 3.0 que tenen com a nexe d'unió una mascota -En Guille- i un determinat territori -pobles de la Selva interior-. Es composa bàsicament de 2 parts: Els textos i dibuixos, realitzats per alumnes dels cicles Mitjà i Superior de 12 CEIPs de la zona Selva-I. que es poden veure també a la WEB del gran llibre de la Selva Les activitats d'explotació dels textos, realitzada pel Centre de Recursos. Al Gran Llibre de la Selva trobareu un munt d'activitats; en concret, 15 lectures, 15 trencaclosques, 15 sopes de lletres i 75 exercicis d'explotació de text.", "Català", "/var/lib/jclic_browser/zips/selva/selva.jclic.inst");

