INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1281", "mayuscul.gif", "Las Mayúsculas","Primaria, Secundaria", "", "Paquete de actividades dónde se trabajan las principales reglas del uso de las mayúsculas. Todas las actividades propiamente dichas van precedidas de una pantalla de información en la que se enuncia la regla que se va a trabajar a continuación. De vez en cuando hay algunas actividades en las que se mezclan varias reglas. El paquete no va dirigido a ningún nivel en concreto, ya que puede ser utilizado bien como iniciación al problemas de las mayúsculas en los niveles más bajos o como repaso en los últimos cursos de Secundaria e incluso Bachillerato.", "Español", "/var/lib/jclic_browser/zips/mayuscul/mayuscul.jclic.inst");

