INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1382", "folkch.gif", "El folklore musical de Chile","Primaria (6-12), Secundaria (12-16)", "Música", "En este paquete de actividades se presentan las formas musicales del folklore Chileno organizadas por zonas: Zona norte, zona central, zona sur y zona insular (isla de Pascua). Entre otras se trabajan la cueca, el trote, el cachimbo el taquirari, la tonada, el corrido, el vals, la música araucana mapuche, la pericona y los cantos de origen polinesio. También se presentan algunos de los instrumentos populares utilizados para interpretar la música.", "español", "/var/lib/jclic_browser/zips/folkch/folkch.jclic.inst");

