INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1470", "ombres.gif", "Ombres del passat","Secundària", "Ciències socials", "Paquet d'activitats on se'ns presenta els fòssils com a prova més clara i contundent de l'existència de l'evolució de les espècies. La realització de les diverses activitats permetrà al alumne adonar-se que les espècies han anat canviant al llarg de la història de la vida. Aquest paquet pertany al conjunt d'aplicacions sobre l'evolució format per Petjades d'home, Canviar per viure, Una gran família, Bricolatge còsmic, Refent el passat i Ombres del passat. Trobareu més informació a la web Petjades d'home: un viatge per la nostra evolució.", "Català", "/var/lib/jclic_browser/zips/ombres/ombres.jclic.inst");

