INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("3191", "hitz25.gif", "Hitz25","Primaria (6-12)", "Lenguas", "Hitz25 es un paquete de actividades clic para trabajar vocabulario básico. Consta de 25 palabras todas ellas con su correspondiente imagen. Además de trabajar el vocabulario también se aprenden 25 oraciones de 3 y 4 palabras, que corresponden con cada imagen. De este modo se aprenden algunos contenidos gramaticales y semánticos elementales. Las actividades están pensadas para alumnos emigrantes que empiezan a aprender euskera.", "euskara", "/var/lib/jclic_browser/zips/hitz25/hitz25.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("3123", "observa2.gif", "Paquet d'activitats d'observació, lògica i raonament","", "Diversos", "Paquet d'activitats on s'exercita l'observació, la lògica i el raonament mitjançant exercicis de comparació, de deducció, d'associació, puzzles, seqüències lògiques, etc. Les activitats estan adreçades principalment a l'Etapa Instrumental Nivell 1 de les Escoles d'Adults", "català", "/var/lib/jclic_browser/zips/observa2/observa2.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1074", "sissolar.gif", "Descobrim el sistema solar","Primària", "Ciències experimentals", "En Llunàtic és un astronauta que us guiarà per l'espai mostrant-vos els planetes, els satèl·lits, els cometes, les constel·lacions, els asteroides, les fases de la lluna, els eclipsis.... L'aplicació s'organitza en tres paquets: 'El sistema solar', 'Els planetes' i 'La terra i la lluna'. Conté animacions i uns enregistraments sonors molt divertits.", "Català", "/var/lib/jclic_browser/zips/sissolar/solar.jclic.inst");

