INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1168", "vocales.gif", "Las vocales","Infantil", "", "Nueva versión con más de 140 actividades! En este paquete de actividades se trabajan las vocales a partir de sonidos, texto e imágenes. Las instrucciones de cada actividad son mensajes sonoros, para dar la máxima autonomía a los alumnos de educación infantil. En los puzzles y en las asociaciones se ha dejado la bandera y el botón de cambio de puzzle para poder repetir varias veces el ejercicio sin correr el riesgo de aprenderlo mecánicamente y no comprensivamente. En el paquete se incluyen algunas actividades adaptadas de la aplicación El ratón de Margarita Fortuny.", "Español", "/var/lib/jclic_browser/zips/vocales/inicio.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
