INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1136", "verbos.gif", "Los verbos - 1","Primaria", "", "¡Nueva versión con más actividades! Paquete de actividades dirigido al ciclo medio de educación primaria donde se trabajan los conceptos básicos relacionados con los verbos: número y persona, tiempos verbales y formas simples y compuestas. La nueva versión de abril del 98 incluye también actividades sobre el verbo HABER y las conjugaciones -AR, -ER e -IR. Encontraréis más información en la web de Aplicaciones educativas de Juan José Mateo.", "Español", "/var/lib/jclic_browser/zips/verbos/verbos.jclic.inst");

