INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2860", "previsio.gif", "Temps de temps: La previsió meteorològica","Primària", "Ciències experimentals", "La previsió meteorològica ens introdueix en aquest món que veiem cada dia a la televisió. Aprenem a distingir els diversos tipus de núvols, a diferenciar un front fred d'un front càlid, a llegir diversos tipus de mapes (isobàrics, radar, temperatures...) i ho fem aprenent que són les isòbares, la seva relació amb els vents i el fronts, el significat de les àrees depressionàries o d'altes pressions, el gradient bàric... Veiem els símbols que els meteoròlegs utilitzen en els mapes que publiquen als diaris i acabem sent capaços de relacionar diverses prediccions meteorològiques amb diversos mapes del temps. És a dir, convertits en tots uns homes o dones del temps. En aquestes pàgines es troben les altres parts de la sèrie temps de temps: L'atmosfera, Observem i mesurem, A bots i barrals i Llamp de rellamp.", "català", "/var/lib/jclic_browser/zips/previsio/previsio.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
