INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1325", "mates2c.gif", "Matemáticas para el 2º Ciclo de Primaria","Primaria", "Matemáticas", "Conjunto de paquetes de actividades de matemáticas para el segundo ciclo de primaria, organizadas en cuatro secciones: - Unidades, decenas, centenas - Restar - Multiplicar - Dividir Las actividades son variadas incluyendo asociaciones gráficas, numéricas, respuesta escrita...", "Vasco", "/var/lib/jclic_browser/zips/eumate2c/eumate2c.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
