INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1164", "pal-llig.gif", "Mayúsculas y minúsculas","Infantil, Primaria", "", "Paquete para conocer y trabajar las diferencias entre la letra ligada y la letra 'de palo'. Se organiza en dos bloques: 'Letras' y 'Nombres'. En el primero se trabajan las letras una por una, y en el segundo se presentan los nombres de los compañeros de clase escritos en los dos tipos de letra. Para cada uno de estos bloques hay tres paquetes: 'Observa', 'Relaciona' y 'Escribe'. En el bloque 'Nombres' aparecen los de los niños y niñas de la escuela en la que se ha desarrollado el paquete. Para utilizarlo correctamente hay que descompactarlo (con el ClicPac) y modificar las actividades para que aparezcan los del grupo al que vaya dirigido.", "Español", "/var/lib/jclic_browser/zips/mayusmin/mayusmin.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2010", "xanrata.gif", "La cueva de Xanrata","Primaria", "Diversos", "Este paquete de actividades ha sido creado a partir de un cuento ilustrado por alumnos de 3º y 4º de primaria sobre dos relatos de la tradición oral de la zona, contados por dos abuelos de alumnos. El paquete empieza con la narración del cuento y continua con diversas actividades (asociaciones, rompecabezas, sopas de letras, texto) que buscan el disfrute en la lectura y una mejor comprensión del texto. Encontraréis una descripción más amplia de la actividad en la web del proyecto Fabulando.", "español", "/var/lib/jclic_browser/zips/xanrata/xanrata.jclic.inst");

