INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("3154", "materia3.gif", "La materia, un estudio educativo","", "Ciencias experimentales", "Actividades para aprender a diferenciar los distintos sistemas materiales de una forma cualitativa, partiendo de su aspecto y llegando a diferenciar entre mezclas y sustancias puras, así como entre elementos y compuestos llegando a utilizar la teoría atómica de una forma muy sencilla. El paquete también trata las técnicas de separación más sencillas tanto para mezclas heterogéneas como disoluciones. Por último, encontramos actividades para conocer los diferentes cambios de estado de la materia así como sus denominaciones.", "español", "/var/lib/jclic_browser/zips/materia3/materia3.jclic.inst");

