INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("3091", "plasgeo1.jpg", "Plasgeo 1","Secundaria", "Matemáticas, Diversos", "Plasgeo 1 es un paquete de actividades que intenta acercar la geometría al alumnado de Educación Plástica y Visual con una oferta muy variada de actividades. Surge como una adaptación del programa Geoclic al nivel de primero de ESO, aunque con una presentación, estructura y selección de actividades diferente. Se divide en tres bloques fundamentales: la línea, los ángulos y los polígonos, y cada bloque consta de un apartado teórico (conceptos) y uno práctico (trazados básicos).", "español", "/var/lib/jclic_browser/zips/plasgeo1/plasgeo1.jclic.inst");

