INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1472", "histori2.gif", "Orígens de la humanitat i antigues civilitzacions","Primària, Secundària", "Ciències socials", "Paquet d'activitats basat en el llibre 'Geografía e historia' de l'editorial SM, destinat a alumnes de 3r cicle de primària i 1r d'ESO, on es treballen dos temes diferents: Els orígens de la humanitat: La prehistòria i les seves grans etapes, l'aparició dels primers homes i dones, la seva vida, per què pintaven les homes i dones del paleolític i la gran evolució de la cultura neolítica. Antigues civilitzacions e la mediterrània: Egipte, els pobles Mesopotàmics (Assíria i Babilònia), les civilitzacions persa i fenícia.", "Català", "/var/lib/jclic_browser/zips/histori2/histori2.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2022", "trastos.gif", "Temps de temps: Observem i mesurem","Primària", "Ciències experimentals", "Aquest és el segon paquet de la sèrie dedicada a la meteorologia. El paquet comença fent conèixer a l'alumne els elements o variables climatològiques que determinen el clima i tot seguit passa a l'estudi dels aparells meteorològics que permeten mesurar i quantificar aquestes variables. Es tracten el aparells més bàsics: quins són, que mesuren i com funcionen. La finalitat dels dos punts anteriors es aconseguir que l'alumne, a banda d'un coneixement teòric, adquireixi les destreses més elementals per tal de fer-les servir de forma correcta i pugui arribar a un control i seguiment de les dades obtingudes que li permetin fer un estudi climatològic força acurat del seu entorn. En aquestes pàgines es troben les altres parts de la sèrie temps de temps: L'atmosfera, La previsió meteorològica, A bots i barrals i Llamp de rellamp.", "català", "/var/lib/jclic_browser/zips/trastos/trastos.jclic.inst");

