INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2051", "elmer.gif", "Aprendiendo a leer con Elmer","Infantil, Primaria", "Lenguas", "Aprendiendo a leer con Elmer es un conjunto de actividades de apoyo al proceso lectoescritor, centradas, sobre todo, en el aspecto fonológico (análisis y síntesis). Las actividades están agrupadas en tres bloques: 'Combinación de unidades lingüísticas', 'Percepción visual' y 'Análisis fonológico'. La adscripción de las actividades a uno u otro bloque conlleva una cierta dosis de arbitrariedad, ya que es muy difícil separar en la práctica los distintos procesos subyacentes a la lectura.", "español", "/var/lib/jclic_browser/zips/elmer/elmer.jclic.inst");

