INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2282", "lenguac3.gif", "Lengua para 3r ciclo de primaria","Primaria", "Lenguas", "Se trata de un conjunto de paquetes de actividades de lengua para el tercer ciclo de primaria. Las actividades se basan en cuatro textos: 'Cuento de la cebolla', 'Relato de los gritos' (Robert Fulghum), 'Las abarcas desiertas' (Miguel Hernández) 'El mercader de burros' (Idries Shan). A partir de los textos se realizan, en primer lugar, actividades de comprensión y además se incluyen, en otros paquetes, actividades de ortografía, clases de palabras, análisis sintáctico y tiempos verbales.", "español", "/var/lib/jclic_browser/zips/lenguac3/lenguac3.jclic.inst");

