INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1068", "vulcnat.jpg", "Vulcanisme i natura a la Garrotxa","Primària, Secundària", "Ciències experimentals", "Aplicació creada pel Camp d'Aprenentatge de la Garrotxa organitzada en cinc paquets: Situació geogràfica: La comarca de la Garrotxa, les poblacions, els espais naturals i la zona volcànica. Processos geològics: Evolució d'un volcà, creació d'un tossol, evolució d'un paisatge volcànic i formació de la cinglera de Castellfollit. El vulcanisme: Parts d'un volcà, materials piroclàstics i el basalt. La vegetació: Vegetació de la zona volcànica, estudi del faig i la fageda d'en Jordà. La fauna: Els mamífers, les aus, els rèptils i els amfibis.", "Català", "/var/lib/jclic_browser/zips/vulcnat/vulcnat.jclic.inst");

