INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("3161", "robahiv.jpg", "Any de neu any de Déu... però que us posareu?","Infantil (3-6), Primària (6-12)", "Llengües", "Paquet de lectoescriptura pensat per als nens i les nenes de finals d'infantil i començaments del cicle inicial de primària. Trobem diferents tipus d'activitats: Veure les imatges (peces de roba) i llegir el nom, així com escoltar-lo en fitxers Wav. Lectura de les diferents peces de roba. Confegir les síl·labes de cada peça de roba. Assenyalar la síl·laba tònica. Confegir els noms de les peces de roba amb totes les seves lletres que la formen. Escriure les lletres que falten. Escriure el nom sencer.", "català", "/var/lib/jclic_browser/zips/robahiv/robahiv.jclic.inst");

