INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2920", "ortograf.gif", "Activitats de llenguatge per al cicle inicial de primària","Primària", "Llengües", "Conjunt de quatre paquets d'activitats de llegua que contenen: Activitats de gramàtica: el nom, l'adjectiu i el verb, identificació, concordances, temps verbal. lèxic sinònims i antònims. Activitats de lectura agrupades per fonemes i grau de dificultat. Activitats de lèxic, el cos, la casa, l'escola i el poble. Activitats d'ortografia agrupades per dificultats. En la guia didàctica del material curricular hi ha també un catàleg de les imatges emprades.", "català", "/var/lib/jclic_browser/zips/clicllen/ortograf.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
