INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1352", "ocamates.gif", "La oca matemática","Primaria", "Matemáticas", "Paquete de actividades destinado a alumnos de primaria. Consiste en un juego de la oca en el que hay que superar una prueba matemática en cada tirada para poder avanzar. Hay 10 partidas predefinidas, cada una de ellas con pruebas distintas. En las partidas aparecen más de 100 ejercicios matemáticos de temática diversa: cálculo, lógica, geometría... Los alumnos de ciclo inicial también pueden utilizar el juego sin realizar las pruebas, solo contando y avanzando en el tablero.", "Gallego", "/var/lib/jclic_browser/zips/ocagal/ocagal.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
