INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1182", "hable.gif", "Habilidades de lectura y escritura","Primaria, Secundaria", "", "Actividades de lengua castellana para la formación básica de las personas adultas, dirigidas a los niveles 1 y 2 de la etapa instrumental. En esta aplicación se han integrado tres conjuntos de paquetes de actividades: FrasesTres grupos de paquetes que se basan en la lectura y memorización de una frase de 2, 3, 4, 5 o 6 palabras ¿ según dificultad y progresión de las actividades- y su posterior escritura. Habilidades de lectura y escrituraTres grupos de paquetes en los que de manera progresiva se introducen los distintos grupos silábicos. La grafía utilizada es de letra mecanoescrita, tanto en mayúsculas como en minúsculas. MisceláneaCinco paquetes con propuestas como ordenar el abecedario, los días de la semana y los meses del año, ordenar letras o palabras según el abecedario, clasificar palabras, relacionar palabras... Se incluye un documento descriptivo en formato PDF.", "Español", "/var/lib/jclic_browser/zips/hable/hable.jclic.inst");

