INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1091", "elecat.jpg", "El elefante y otros grandes animales","Infantil, Primaria", "Ciencias experimentales", "Paquete de actividades destinado a Educación infantil y a primer ciclo de Educación Primaria donde se trabajan los animales grandes: el elefante, el hipopótamo, la jirafa y el rinoceronte. En este paquete encontramos actividades de asociación, de identificación de partes del elefante y rompecabezas. La actividad adjunta un fichero de ayuda donde se explica los tipos de actividades y los niveles a que pertenecen.", "Español", "/var/lib/jclic_browser/zips/elecast/elecast.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2068", "pasamat.gif", "Pasatiempos matemáticos","Primaria, Secundaria", "Matemáticas", "Aquellos que se toman el juego como un simple juego y el trabajo con excesiva seriedad, no han comprendido mucho ni de uno ni de otro. H. Heine, poeta alemán. Este paquete de actividades está basado en el programa 'JUEGOMAT', programa que presenta un compendio de actividades matemáticas enfocadas desde su aspecto lúdico, pretendiendo ser un juego educativo dentro de lo que se ha dado en llamar 'Matemática Recreativa'. JUEGOMAT permite realizar un conjunto de ejercicios dentro del área de matemáticas bajo un mismo entorno de trabajo. Se han añadido ejercicios distintos a los que incorpora el programa JUEGOMAT y también se han reducido en su número para que la adaptación a CLIC fuese posible. Los ejercicios planteados pueden ser utilizados, según sus niveles de complejidad, dentro del Segundo y Tercer Ciclo de Educación Primaria y del Primer Ciclo de la Educación Secundaria Obligatoria.", "euskara", "/var/lib/jclic_browser/zips/pasameus/pasameus.jclic.inst");

