INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1125", "eusdemo.gif", "Eusdemo (demo euskera)","Primaria, Secundaria", "", "Aditz laguntzailea, sinonimoak, antonimoak, esaera zaharrak, testuaren koherentzia (puzzleen bitartez) eta aditzaren laguntzailea irudien bidez lantzen da. Inguruaren ezaguerari buruzko adibeetan giza-gorputzaren atalak jorratzen dira. Ejemplos de actividades clic en euskera. Trabajan el auxiliar del verbo, sinónimos, antónimos, refranes, coherencia en un texto (por medio de puzzles), el uso del auxiliar del verbo apoyado por imágenes que significan la persona y su acción (tanto el sujeto, como el objeto directo e indirecto). Referente al área del conocimiento del medio se han presentado algunos ejercicios que trabajan partes del cuerpo humano.", "Vasco", "/var/lib/jclic_browser/zips/eusdemo/eusdemo.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1213", "qhe.gif", "¿Qué hora es?","Primaria", "", "Conjunto de actividades para trabajar los aspectos relacionados con la medida el tiempo, elemento integrante de la programación de matemáticas del ciclo medio de educación primaria. La actividad consiste en un secuencia de tres paquetes en los cuales se desarrollan de manera sistemática aspectos como: el reloj, sus partes, las unidades pequeñas de medida del tiempo, los diferentes tipos de reloj, la manera correcta de leer la hora en Catalán, las diferentes maneras de escribir la hora (inclusive la forma matemática o digital) y una batería de problemas específicos sobre el tema. Aunque el tema corresponde específicamente a el área de matemáticas, se ha querido incidir especialmente en los aspectos relacionados con la lengua. La dificultad de la lectura correcta de la hora en catalán, unida a la potencia del castellano, ha hecho que se dediquen muchas actividades a estos aspectos. Se trabaja el vocabulario específico y, mediante las actividades de escritura, se ha querido insistir sobre la ortografía. Quiero expresar mi agradecimiento a Javier Rodríguez, de la escuela l'Alzina, de Molins de Rei, por autorizarme a hacer uso de las imágenes creadas por él para su trabajo, El Reloj.", "Vasco", "/var/lib/jclic_browser/zips/zod/rellotg0.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
