INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1418", "familia.jpg", "Una gran família","Primaria, Secundaria", "Ciencias sociales", "Las actividades que encontraréis en este paquete intentan mostrar la existencia de semejanzas y diferencias entre los diversos seres vivos, haciendo especial referencia al hombre y el mono. A partir de este hecho se reflexiona sobre la conveniencia y necesidad de una ordenación y clasificación, que nos lleva a darnos cuenta de que todos formamos una gran familia. El paso siguiente es sugerir la posibilidad de saltos entre las ramas: la evolución. Este paquete pertenece al conjunto de aplicaciones sobre la evolución formado por Petjades d'home, Canviar per viure, Una gran família, Bricolatge còsmic, Refent el passat y Ombres del passat. Encontraréis más información en la web Petjades d'home: un viatge per la nostra evolució.", "Vasco", "/var/lib/jclic_browser/zips/sendia/sendia.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1380", "patum.jpg", "La Patum de Berga","Primària, Secundària", "Música", "Aquest paquet d¿activitats té com a finalitat fer conèixer la Patum de Berga. Comença amb un text on s'expliquen les característiques d'aquesta festa i continua amb un conjunt d'activitats on es poden veure fotografies i escoltar la música de les comparses: Turcs i cavallets, Maces, Guites, L'àliga, Els gegants, Nans vells, Nans nous, Plens i Tirabols. El paquet es complementa amb trencaclosques, jocs i sopes de lletres.", "Català", "/var/lib/jclic_browser/zips/patum/patum.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2145", "projecl.gif", "Grups consonàntics PL, BL, FL, GL, CL","Primària", "Llengües", "Aquest paquet d'activitats està adreçat a Educació Infantil o Primària (per a alumnes amb dificultats en la discriminació, en la pronúncia i/o en l'escriptura de paraules que contenen els grups consonàntics: pl, bl, fl, gl, cl). El paquet està format per activitats progressives on es treballen paraules que contenen aquests grups consonàntics. Es treballen els sínfons a l'inici i al mig de les paraules, i amb reforç auditiu o sense depenent de l'activitat.", "català", "/var/lib/jclic_browser/zips/projecl/projecl.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1377", "flauta.gif", "La flauta Màgica","Primaria, Secundaria", "Música", "Paquete de actividades sobre la ópera de Mozart 'La flauta Mágica'. En este se trabajan el argumento, la música y los personajes de la obra. Utiliza pequeños trozos de la música de Mozart para ayudar a explicar los contenidos. Las actividades son variadas: puzzles, sopas de letras, preguntas y respuestas...", "Español", "/var/lib/jclic_browser/zips/flautaes/flautaes.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
