INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1345", "formas.gif", "Formas","Infantil", "Matemáticas", "FORMAS es una aplicación para Educación Infantil. Contiene lenguaje escrito (mínimo) y oral en lengua gallega. Se compone de dos niveles. En el primero hay 22 actividades sobre las 3 formas básicas: círculo, triángulo y cuadrado. En el segundo nivel hay 18 actividades sobre las formas anteriores además de rectángulo y rombo. Con esta aplicación se pretende que el niño se familiarice con las formas básicas que se trabajan en Infantil y mejore su identificación y reconocimiento en objetos de la vida cotidiana.", "Vasco", "/var/lib/jclic_browser/zips/euformas/euformas.jclic.inst");

