INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("2644", "chillot.gif", "Homenaje a Jorge Oteiza y Eduardo Chillida","Secundaria", "Ciencias sociales", "Este paquete quiere ser un homenaje a dos grandes artistas vascos: Jorge Oteiza y Eduardo Chillida. En él se explican los aspectos mas significativos de sus biografías, la fundamentación conceptual y estética de su obra y un repaso a sus proyectos y creaciones mas relevantes.", "español", "/var/lib/jclic_browser/zips/chillot/chillot.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1039", "eskubide.gif", "Umeen Eskubideak","Primaria", "Diversos", "Actividad basada en un programa que la UNICEF ha venido desarrollando bajo el nombre de LA VOZ DE LOS NIÑOS. Consta de 12 secuencias (4 ejercicios en cada una) basadas en cada uno de los derechos que allí se enmarcan: identidad, familia, salud, educación, juego, igualdad, protección, participación, bienestar, paz, los niños primero, solidaridad. Para terminar otras tres que sirven de compendio de toda la actividad (escribir una carta y un texto descriptivo).", "Vasco", "/var/lib/jclic_browser/zips/eskubide/eskubide.jclic.inst");

INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
