INSERT INTO jclic (id,image,actividad,edad,materia,descripcion,idioma,archivo)
  VALUES ("1126", "aditzak.gif", "Aditzak (Los verbos en eusquera)","Primaria, Secundaria", "", "1200 esaldietan era ezberdinetan antolatuak, ikasleak esaldi bakoitzari dagokion laguntzailea jarri beharko du. Esaldiak euskal aditza antolatua dagoen bezala antolatuta daude. (Nor, Nor-Nork, Nor-Nori, Nor-Nori-Nork; orainaldian, lehenaldian eta ahaleran). En 1200 frases organizadas de formas diferentes, se trata de que el alumno utilice adecuadamente el auxiliar que corresponda en la frase. Estas están estructuradas como corresponde al verbo en eusquera.", "Vasco", "/var/lib/jclic_browser/zips/aditzak/aditzak.jclic.inst");

